CKEDITOR.editorConfig = function( config ) {
	// Define changes to default configuration here. For example:
	// config.language = 'fr';
	// config.uiColor = '#AADC6E';
    config.filebrowserBrowseUrl = 'explorer/explore.html';
    config.width = '100%';     // 500 pixels wide.
    config.height = 400;
    //config.language = 'si';
    config.skin = 'office2013';
    config.toolbar = [
        ['Styles','Format','Font','FontSize','Image','Table','-','Link','Flash','Smiley','TextColor','BGColor','Source'],
        '/',
        ['Bold','Italic','Underline','StrikeThrough','-','Undo','Redo','-','Cut','Copy','Paste','Find','Replace','-','Outdent','Indent','-','Print','NumberedList','BulletedList','-','JustifyLeft','JustifyCenter','JustifyRight','JustifyBlock']
    ]
};
